//multer parses the files/documents sent by the user (as the browser semds them encrypted) and uploads the file in the the destination with the name givenin the file name

import multer from 'multer'


//imagine cb as if it is answrint the question asked by the keys(destination, filename)
//cb is a function generated by node itself.
const storage= multer.diskStorage({
    destination: function(res,file,cb){ //destination means where to keep the files
        cb(null,"./Public/temp")
    },
    filename: function(res,file,cb){
        cb(null,file.originalname)
    }
})

export const upload=multer({
    storage
})

